$(document).ready(function() {
  // Écouter le clic sur le bouton pour générer le code QR
  $("#generate").on("click", function() {
    // Récupérer les données saisies par l'utilisateur
    var donnees = $("#data").val();

    // Récupérer la taille sélectionnée par l'utilisateur
    var taille = $("#size").val();

    // Vérifier si les données saisies ne sont pas vides
    if (donnees !== "") {
      // Générer le code QR en utilisant les données saisies et la taille sélectionnée
      $("#qrcode").empty().qrcode({
        text: donnees,
        width: taille,
        height: taille
      });
    }
  });
});